mapper.select:
select cid,cname,num,remake,weekday,time,address,lesson from course
where uid=#{uid}
and cname like #{cname}
and status='1'

select COUNT(*) from course
where weekday=#{weekday}
and time =#{time}
and address=#{address}
and status='1'

SELECT COUNT(*) from course
where weekday=#{weekday}
and time =#{time}
and address=#{address}
and status='1'

select count(*) from course c
LEFT join easyuser su on c.uid =su.id
where c.time=#{time}
and c.weekday=#{weekday}
and su.username =#{username}

select c.cid,c.cname,c.num,c.remake,c.weekday,c.time,c.address,c.lesson,s.username
 from course c left join easyuser s on c.uid=s.id
 where cname like #{cname}
 and status='0'
  and s.username like #{username}

select c.cid,c.cname,c.num,c.remake,c.weekday,c.time,c.address,c.lesson,s.username
from course c left join easyuser s on c.uid=s.id
where cname like #{cname}
and status='1' and s.id in(
select id FROM easyuser
where username like #{username}
)
and c.num>0
ORDER BY weekday,time

SELECT count(*) from source s
left join course c on s.cid=c.cid
where c.weekday=#{weekday}
and c.time=#{time}
and c.status='1'
and c.num>0
and s.username=#{stuName}

mapper.delete:
DELETE from source
where cid=#{cid}
and username=#{stuName}

mapper.select:
select su.username,su.email from easyuser su
left join source s on s.username=su.username
where s.cid=#{cid}

select c.cname from course c
LEFT JOIN source s on s.cid =c.cid
LEFT JOIN easyuser su on s.username=su.username
where  su.username=#{username}
and c.weekday =#{weekday}
and c.time=#{time}

select c.cname from course c
LEFT JOIN easyuser su on su.id=c.uid
where  su.username=#{username}
and c.weekday =#{weekday}
and c.time=#{time}
and c.status="1"
